import React, { useState } from 'react';
import { db } from "../../firebase";

const createPost = () => {
    const data = {
        direction: "",
        message: "",
        author: "",
        uid: new Date().getTime(),
    };

    // adding data here
    db.collection("posts")
      .doc(data.uid.toString())
      .set(data)
      .then(() => {
        console.log("A new post has been added", "Success");
        window.location = "/";
      })
      .catch(error => {
        console.log(error.message, "Create post failed");
      });
  };


const Render = () => {

    return (
        <div className='bg-gray-100'>

            <div className='border bg-white p-2 border-gray-300 leading-normal text-lg font-medium'>
                <div className='mb-2 font-bold text-xl'>Dogecoin to go down?</div>
                <div className='mb-2'>A Goldman Sachs executive just quit after making millions from Dogecoin. This is bullish because the media reports it and brings awareness.</div>
                <div className='mb-2 text-base text-gray-400 leading-relax'>analysis by @irish_voxel</div>
            </div>
            <div className='border'>
                <div className=''>Sign of market crash?</div>
                <div className=''>The resurgence of random altcoins exploding in value is a sure sign that market-crash is imminent.</div>
                <div className=''>analysis by @drewrostron</div>
            </div> 
            <Link to="/apply-now" className='cursor-pointer bg-black text-white text-center rounded-lg mt-5 mb-10 w-full py-3 px-4'>Create a post!</Link>
        </div>
    );
}

export default Render

