import React, { useState } from "react";
import { Link } from 'react-router-dom';
import { db } from "../../firebase";

const CreatePost = () => {

    const [author, setAuthor] = useState('');
    const [message, setMessage] = useState('');
    const [direction, setDirection] = useState('');
    const [status, setStatus] = useState('Publish');

    const data = {
        direction: "", message: "", author: "",
        uid: new Date().getTime(),
    };

    const handleSubmit = (evt) => {
        evt.preventDefault();

        setStatus('Submitting');

        db.collection("posts")
        .doc(data.uid.toString())
        .add(data)
        .then(() => {
            console.log("A new post has been published", "Success");
            window.location = "/";
          })
          .catch(error => {
            console.log(error.message, "Publish post failed");
          });
      };

    return (
        <form onSubmit={handleSubmit}>
            <div className=''>

            <div className=''> 
                <label></label>
                    <div className="w-full">
                        <input type="text" value={message} onChange={e => setMessage(e.target.value)} required placeholder="Type your analysis content in here..."
                            className="border py-3 px-4 bg-white rounded-lg placeholder-gray-400 text-gray-900 appearance-none inline-block w-full focus:outline-none focus:ring-2 focus:ring-blue-600"
                        />
                        <div className='text-sm text-gray-500 leading-normal'>Keep it short.</div>
                    </div>
                </div>

                <div className=''>
                    <label>Type your snapchat username</label>
                    <div className="w-full">
                        <input type="text" value={author} onChange={e => setAuthor(e.target.value)} required placeholder="@jane_doe247"
                            className="border py-3 px-4 bg-white rounded-lg placeholder-gray-400 text-gray-900 appearance-none inline-block w-full focus:outline-none focus:ring-2 focus:ring-blue-600"
                        />
                        <div className='text-sm text-gray-500 leading-normal'>Your snapchat name will appear to others.</div>
                    </div>
                </div>

                <button className='cursor-pointer bg-black text-white text-center rounded-lg mt-5 mb-10 w-full py-3 px-4'>{status}</button>

            </div>
        </form>
    )
  }

const Render = () => {

    return (
        <div>
            <div className='p-8'>

                <Link to="/" className='cursor-pointer my-2 underline text-blue-600 hover:text-blue-800 underline visited:text-purple-600'>back to homepage</Link>
                <div className='text-4xl font-bold leading-normal my-4'>Create a new post</div>

                <CreatePost />

            </div>
        </div>
    )
}

export default Render
