import React, { useState, useEffect, useRef } from "react";
import moment from 'moment';
import { db } from "../../firebase";

const DropDown = (uid) => {
    const [open, setOpen] = useState(false);
    const drop = useRef(null);

    const handleClick = (e) => {
        if (open && !e.target.closest(`${drop.current.className}`)) {
          setOpen(false);
        }
      }

    /*const handleDelete = () => {
        const id = uid.uid.toString()
        console.log(id)
        db.collection('post')
        .doc(id)
        .delete()
        .then(() => {
            console.log("Document successfully deleted!");
            window.location.reload();
        }).catch((error) => {
            console.error("Error removing document: ", error);
        });
    }*/

    const handleDelete = () => {
        const id = uid.uid
        db.collection("posts")
        .where('uid', "==", id)
        .get()
        .then( (querySnapshot) => {
            console.log(querySnapshot.docs[0].ref)
            querySnapshot.docs[0].ref.delete()
            console.log("Document successfully deleted!")
        }).catch((error) => {
            console.error("Error removing document: ", error);
        });
    }

    const Modal = () => {
            return (
                <div className={'absolute flex flex-col text-center ring-1 ring-gray-300 text-black bg-white rounded-xl right-6 text-base'}>
                    <div className='cursor-pointer p-1 px-4 py-3 rounded-lg hover:bg-gray-100 m-2'>Update</div>
                    <div className='border-b broder-gray-200'></div>
                    <div className='cursor-pointer p-1 px-4 py-3 rounded-lg hover:bg-gray-100 m-2' onClick={() => handleDelete()}>Delete</div>
                </div>
            );
    }

    useEffect(() => {
        document.addEventListener("click", handleClick);
        return () => {
          document.removeEventListener("click", handleClick);
        };
    });


    return(
        <div ref={drop} className='dropdown'>
            <button className='hover:text-black hover:bg-gray-100 rounded-xl p-2' onClick={() => setOpen(open => !open)}><svg class="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M5 12h.01M12 12h.01M19 12h.01M6 12a1 1 0 11-2 0 1 1 0 012 0zm7 0a1 1 0 11-2 0 1 1 0 012 0zm7 0a1 1 0 11-2 0 1 1 0 012 0z"></path></svg></button>
            {open && <Modal uid={uid}/>}
        </div>
    );
}



    const Render = () => {

    const [data, setData] = useState();

    useEffect(() => {
        const update = db
            .collection("posts")
            .orderBy("uid", "desc")
            /*
            .get()
            .then(querySnapshot => {
                const data = querySnapshot.docs.map(doc => doc.data());
                setData(data);
            */
            .onSnapshot(documentSnapshot => {
                const data = documentSnapshot.docs.map(doc => doc.data());
                console.log(data)
                setData(data);
            });
        return () => update();
    }, []);

    const getTime = (uid) => {
        moment().format();
        return (
            moment.utc(uid).local().startOf('seconds').fromNow()
        )
    }

    const item = data?.map((data) =>
        <div key={data.uid} className='flex flex-col bg-white rounded-3xl mb-5 p-5'>

            <div className='inline-flex items-center text-base mb-5'>
                <div className='relative bg-gray-300 rounded-full w-max p-5'>
                    <div className='absolute -m-3 text-base text-gray-400'>🚀</div>
                </div>
                <div className='ml-4 text-sm'>
                    <div className=''><b>{data.author.toLowerCase()}</b></div>
                    <div className='text-gray-400'>{getTime(data.uid)}</div>
                </div>   
                <div className='ml-auto'><DropDown uid={data.uid}/></div>
            </div>

            <div className='flex flex-col w-full'>
                <div className='mb-7'>{data.message}</div>
                <div className='inline-flex text-gray-400 items-center text-base'>
                    <div className='inline-flex items-center bg-gray-100 rounded-lg px-2 py-1 mr-4'>
                        <div><svg class="w-4 h-4 mx-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M5 10l7-7m0 0l7 7m-7-7v18"></path></svg></div>
                        <div>0</div>
                        <div><svg class="w-4 h-4 mx-2" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path stroke-linecap="round" stroke-linejoin="round" stroke-width="2" d="M19 14l-7 7m0 0l-7-7m7 7V3"></path></svg></div>
                    </div>
                </div>
            </div>

        </div>
    );

    return (
        <div className='p-4 text-lg font-medium leading-relax h-screen'>

            <div className='bg-gradient-to-tl from-purple-700 via-pink-500 to-red-500 p-4 -m-4 text-white pt-16 mb-4'>
                <div className='font-bold text-3xl'>Forecast🧐</div>
                <div className='font-bold text-base mb-8'>Latest predictions from our analysts</div>
            </div>

            <div className='rounded bg-white -m-4 -my-10 p-4 pt-5 bg-gray-100'>
                {item}
            </div>
        </div>
    );
}

export default Render



