import React, { useState, useEffect, useRef } from "react";
import { Link, useHistory } from 'react-router-dom';

const RenderModal = () => {
    const history = useHistory()
    const [open, setOpen] = useState(false);
    const drop = useRef(null);

    const handleClick = (e) => {
        if (open && !e.target.closest(`${drop.current.className}`)) {
          setOpen(false);
        }
      }

    const Modal = () => {
            return (
                <div className='font-medium text-lg'>
                    <div className='fixed bg-black w-full inset-0 opacity-60'></div>

                    <div className='fixed flex flex-col bg-white w-2/3 inset-0 p-2'>
                        <div className='flex items-center p-5 h-16 font-bold text-xl bg-white border-b border-gray-200 mb-5 -m-2'>
                            Irrational Investors
                        </div>

                        <div className='rounded-lg bg-gray-50 border mb-5 p-4'>
                            <div className='font-bold text-xl mb-4'>Irrational Investors is a dedicated group of analytical traders</div>
                            <div className='mb-8'>We're a global platform where investors collaborate, learn and refine their trading skills.</div>
                            <div className='mb-4 bg-indigo-600 hover:bg-indigo-900 text-white text-center rounded p-2.5'>Create an account</div>
                            <div className='hover:bg-gray-200 text-indigo-700 text-center rounded p-2.5'>Log in</div>
                        </div>

                        <div className='flex flex-col w-full mb-5'>
                            <div onClick={() => history.push('./')} className='p-2 hover:bg-gray-200 rounded mb-2'>🏡 Home</div>
                            <div onClick={() => history.push('./feed')} className='p-2 hover:bg-gray-200 rounded mb-2'>📈 Forecasts</div>
                            <div onClick={() => history.push('./members')} className='p-2 hover:bg-gray-200 rounded mb-2'>🧑‍🚀 Members</div>
                            <div onClick={() => history.push('./members')}  className='p-2 hover:bg-gray-200 rounded mb-2'>📚 FAQ</div>
                            <div className='p-2 hover:bg-gray-200 rounded mb-2'>🤓 About</div>
                        </div>
                    </div>

                </div>
            );
    }

    useEffect(() => {
        document.addEventListener("click", handleClick);
        return () => {
          document.removeEventListener("click", handleClick);
        };
    });


    return(
        <div ref={drop} className='flex'>
            <button onClick={() => setOpen(open => !open)}><svg class="w-8 h-8 my-auto mx-2 p-1" fill="none" stroke="currentColor" viewBox="0 0 24 24" xmlns="http://www.w3.org/2000/svg"><path stroke-linecap="round" stroke-linejoin="round" stroke-width="3" d="M4 6h16M4 12h16M4 18h7"></path></svg></button>
            {open && <Modal />}
        </div>
    );
}

const Render = () => {
    return (
        <div className='relative bg-white z-50'>
            <div className='border-b border-gray-200 bg-white fixed w-full inline-flex justify-end py-3 px-2 h-16'>
                <RenderModal />
                <div className='items-start mr-auto font-bold text-xl my-auto'>Investors</div>
                <Link to="/create" className='mx-3 items-start cursor-pointer bg-indigo-600 hover:bg-indigo-800 text-white font-semibold text-center rounded px-4 py-2'>Create</Link>
            </div>
            <div className='h-16'></div>
        </div>
    )
}

export default Render
