import React, { useState, useEffect } from "react";
import { Link } from 'react-router-dom';
import { db } from "../../firebase";

const Render = () => {

    const [data, setData] = useState();

    useEffect(() => {
        db.collection("posts")
        .get()
        .then(querySnapshot => {
            const data = querySnapshot.docs.map(doc => doc.data());
            setData(data);
        });
    }, []);

    const item = data?.map((data) =>
        <div key={data.uid} className='bg-white rounded-xl ring-1 ring-gray-300 py-2 px-4 pl-8 mb-4'>
            <div className='my-2 text-base text-gray-400'>analysed by {data.author}</div>
            <div className='my-2'>{data.message}</div>
        </div>
    );

    return (
        <div className='p-4 text-lg font-medium leading-relax'>

            <div className='bg-blue-700 p-4 -m-8 text-white pt-12 mb-4'>
                <div className='font-bold text-3xl mb-4'>Trending newsðŸ”¥</div>
            </div>
            <div className='rounded-xl bg-white -m-4'>{item}</div>
        </div>
    );
}

export default Render



